import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class CiclePainter extends CustomPainter {  @override  void paint(Canvas canvas, Size size) {    final height = size.height;    final width = size.width;    // 创建画笔    canvas.translate(0, 0);    final Paint paint = Paint();    paint.color = Colors.white;    Path path = Path();    path      ..moveTo(0, 0)      ..lineTo(width, 0)      ..lineTo(width, height)      ..lineTo(0,height)      ..close();    var pathOval =Path()..addOval(Rect.fromCenter(center: Offset(width/2, height/2),width: width,height: width));    canvas.drawPath(Path.combine(PathOperation.difference, path, pathOval), paint);  }  @override  bool shouldRepaint(CustomPainter oldDelegate) => false;}